// src/components/DragonListPage/DragonListPage.tsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  Container,
  ResponsiveTable,
  FirstColumn,
  SecondColumn,
  ThirdColumn,
  ActionsColumn,
} from "./styles";
import DragonFormModal from "../DragonFormModal/DragonFormModal";

interface Dragon {
  id: string;
  name: string;
  type: string;
  createdAt: string;
  histories: string[];
}

const DragonListPage: React.FC = () => {
  const [dragons, setDragons] = useState<Dragon[]>([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedDragon, setSelectedDragon] = useState<Dragon | null>(null); // Adicionado estado para o dragão selecionado

  useEffect(() => {
    const fetchDragons = async () => {
      try {
        const response = await axios.get(
          "http://5c4b2a47aa8ee500142b4887.mockapi.io/api/v1/dragon"
        );
        setDragons(response.data);
      } catch (error) {
        console.error("Error fetching dragons:", error);
      }
    };

    fetchDragons();
  }, []);

  const handleRemoveDragon = async (dragonId: string) => {
    // Implemente a remoção conforme necessário
  };

  const handleViewDetails = async (dragonId: string) => {
    try {
      const response = await axios.get(
        `http://5c4b2a47aa8ee500142b4887.mockapi.io/api/v1/dragon/${dragonId}`
      );
      setSelectedDragon(response.data);
      setIsModalOpen(true);
    } catch (error) {
      console.error("Error fetching dragon details:", error);
    }
  };

  const handleSaveDragon = async (
    name: string,
    type: string,
    histories: string[]
  ) => {
    // Implemente a adição ou edição conforme necessário
  };

  return (
    <Container>
      <h2>Dragon List</h2>
      <DragonFormModal
        isOpen={isModalOpen}
        onClose={() => {
          setIsModalOpen(false);
          setSelectedDragon(null);
        }}
        onSave={handleSaveDragon}
        dragon={selectedDragon} // Adicionado propriedade dragon para exibir detalhes no modal
      />
      <ResponsiveTable>
        <thead>
          <tr>
            <FirstColumn>Name</FirstColumn>
            <SecondColumn>Type</SecondColumn>
            <ThirdColumn>Data de Criação</ThirdColumn>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {dragons.map((dragon) => (
            <tr key={dragon.id}>
              <td>{dragon.name}</td>
              <td>{dragon.type}</td>
              <td>{new Date(dragon.createdAt).toLocaleString()}</td>
              <ActionsColumn>
                <button onClick={() => handleViewDetails(dragon.id)}>
                  Details
                </button>
                {/* Adicione outros botões de ação conforme necessário */}
              </ActionsColumn>
            </tr>
          ))}
        </tbody>
      </ResponsiveTable>
    </Container>
  );
};

export default DragonListPage;
