// src/components/DragonFormModal/styles.ts
import styled from "styled-components";

export const FormContainer = styled.div`
  width: 300px; /* Largura desejada para a faixa do lado direito */
  height: 100vh;
  position: fixed;
  right: 0;
  top: 0;
  background-color: #fff;
  padding: 16px;
  box-shadow: -10px 0 20px rgba(0, 0, 0, 0.1); /* Sombra para destacar a faixa */

  form {
    display: flex;
    flex-direction: column;
    gap: 8px;

    label {
      display: flex;
      flex-direction: column;
    }

    button {
      background-color: #007bff;
      color: #fff;
      padding: 8px;
      border: none;
      border-radius: 4px;
      cursor: pointer;
    }
  }
`;
