// src/components/DragonDetailsModal/DragonDetailsModal.tsx
import React from "react";
import Modal from "react-modal";
import { DragonListPage } from "../DragonListPage/DragonListPage";

interface DragonDetailsModalProps {
  isOpen: boolean;
  dragon: Dragon | null;
  onClose: () => void;
}

const DragonDetailsModal: React.FC<DragonDetailsModalProps> = ({
  isOpen,
  dragon,
  onClose,
}) => {
  if (!dragon) {
    return null;
  }

  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onClose}
      style={{
        content: {
          top: "50%",
          left: "50%",
          right: "auto",
          bottom: "auto",
          marginRight: "-50%",
          transform: "translate(-50%, -50%)",
        },
      }}
    >
      <div>
        <h3>Detalhes do Dragão</h3>
        <p>Name: {dragon.name}</p>
        <p>Type: {dragon.type}</p>
        <p>Data de Criação: {new Date(dragon.createdAt).toLocaleString()}</p>
      </div>
    </Modal>
  );
};

export default DragonDetailsModal;
