import React, { useState, useEffect } from "react";
import Modal from "react-modal";
import { FormContainer, CloseButton, AddButton, SelectInput } from "./styles";

interface DragonFormModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSave: (name: string, type: string) => void;
}

const DragonFormModal: React.FC<DragonFormModalProps> = ({
  isOpen,
  onClose,
  onSave,
}) => {
  const [name, setName] = useState("");
  const [type, setType] = useState("");

  useEffect(() => {
    if (isOpen) {
      setName("");
      setType("");
    }
  }, [isOpen]);

  const handleSave = () => {
    onSave(name, type);
    onClose();
  };

  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onClose}
      contentLabel="Add Dragon"
      overlayClassName="Overlay"
      className="Modal"
    >
      <FormContainer>
        <CloseButton onClick={onClose}>&times;</CloseButton>
        <h2>Add Dragon</h2>
        <form>
          <label>
            Name:
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </label>
          <label>
            Type:
            <SelectInput value={type} onChange={(e) => setType(e.target.value)}>
              <option value="">Select Type</option>
              <option value="Fire">Fire</option>
              <option value="Ice">Ice</option>
              <option value="Water">Water</option>
            </SelectInput>
          </label>
          <button type="button" onClick={handleSave}>
            Save
          </button>
        </form>
      </FormContainer>
    </Modal>
  );
};

export default DragonFormModal;
