// src/components/DragonFormModal/DragonFormModal.tsx
import React, { useState } from "react";
import Modal from "react-modal";
import { FormContainer } from "./styles";

interface DragonFormModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSave: (name: string, type: string) => void;
}

const DragonFormModal: React.FC<DragonFormModalProps> = ({
  isOpen,
  onClose,
  onSave,
}) => {
  const [name, setName] = useState("");
  const [type, setType] = useState("");

  const handleSave = () => {
    onSave(name, type);
    onClose();
  };

  return (
    <Modal isOpen={isOpen} onRequestClose={onClose} contentLabel="Add Dragon">
      <FormContainer>
        <h2>Add Dragon</h2>
        <form>
          <label>
            Name:
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </label>
          <label>
            Type:
            <input
              type="text"
              value={type}
              onChange={(e) => setType(e.target.value)}
            />
          </label>
          <button type="button" onClick={handleSave}>
            Save
          </button>
        </form>
      </FormContainer>
    </Modal>
  );
};

export default DragonFormModal;
