// src/components/LoginPage/LoginPage.tsx
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../../context/AuthContext";
import { Container, LoginForm, Input, Button } from "./styles";

const LoginPage: React.FC = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleLogin = () => {
    if (username === "admin" && password === "Pass@2024") {
      console.log("Login bem-sucedido");
      login();
      navigate("/dragons"); // Redireciona automaticamente para a DragonList após o login
    } else {
      console.log("Login falhou");
      // Aqui você pode exibir uma mensagem de erro para o usuário, se desejar
    }
  };

  return (
    <Container>
      <LoginForm>
        <h2>Login</h2>
        <Input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <Input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <Button onClick={handleLogin}>Login</Button>
      </LoginForm>
    </Container>
  );
};

export default LoginPage;
